mapper.select:
 		SELECT * FROM 
			(SELECT rownum r ,f.* from
				(SELECT * 
				 FROM notice  

				 WHERE ${column} LIKE '%' || #{findText} || '%'


				 WHERE TO_CHAR(createdAt,'YYYY-MM-DD') = #{findText}

				 ORDER BY idx DESC) f
		 	)
		WHERE r BETWEEN #{startNo} AND #{endNo}

SELECT count(*) FROM notice
WHERE ${column} LIKE '%' || #{findText} || '%'
WHERE TO_CHAR(createdAt,'YYYY-MM-DD') = #{findText}
